    def login(self):
        account = self.account.text()
        password = self.password.text()

        if account == '' or password == '':
            QMessageBox.warning(self.copy, 'error', '账号或密码不能为空',
                                QMessageBox.Yes, QMessageBox.Yes)
            return

        response = login_teacher(account, password)
        if response == 'TEACHER_LOGIN_SUCCESS':
            # 注意，只有添加self声明才能正常显示下一个界面
            self.window = ComboxDemo(account)
            self.window.show()

            self.copy.close()
            self.copy.destroy()

        elif response == 'TEACHER_LOGIN_FAIL':
            self.tip.setText('账号或密码错误，请重试!')
            self.tip.setStyleSheet('color: red;')
            self.tip.setAlignment(Qt.AlignCenter)

    def register(self):
        account = self.account.text()
        password = self.password.text()

        if account == '' or password == '':
            QMessageBox.warning(self.copy, 'error', '账号或密码不能为空',
                                QMessageBox.Yes, QMessageBox.Yes)
            return

        response = register_teacher(account, password)

        if response == 'TEACHER_ACCOUNT_REPEATED':
            self.tip.setText('账号已存在')
            self.tip.setStyleSheet('color: red')
            self.tip.setAlignment(Qt.AlignCenter)

        elif response == 'TEACHER_REGISTER_SUCCESS':
            self.window = ComboxDemo(account)
            self.window.show()

            self.copy.close()
            self.copy.destroy()


